function main() {
  let trs = [...document.querySelectorAll(".tbl_m_schedule tr")].reverse();
  rws.log(`[${document.URL}] Table Row Count: ${trs.length}`)

  let data = [];
  let tr, day, wd, time, content, note;
  let rspan = 0, rspan_t = 0;

  // let i = 0;
  while (tr = trs.pop()) {
    // rws.log(i++)
    let tds = [...tr.children].reverse()
    let td = tds.pop()
    if (td === undefined  ||  td.tagName != "TD") continue;

    if (rspan == 0) {
      if (td.id.startsWith("d-")) {
        day = td.innerText;
        if (td.hasAttribute("rowspan")) rspan = td.rowSpan;
        else rspan = 0;
        td = tds.pop();

        wd = td.innerText;
        td = tds.pop();
      }
      else day = wd = null;
    }
    // rws.log("rspan: " + rspan)
    if (rspan > 0) {
      --rspan;
      if (!td.hasAttribute("id")) {
        time = td.innerText;
        rspan_t = td.rowSpan;
        td = tds.pop();
      }
      if (--rspan_t < 0) time = null;
      content = td.innerText;
      td = tds.pop();

      note = td.innerText;
      td = tds.pop();

      data.push({"day": day, "wd": wd, "time": time, "content": content, "note": note})
    }
  }

  let scriptAction = {
    data: data,
    nextPage: {
      buttonPath: null,
      waitElemPath: null,
    }
  };
  rws.resolve(scriptAction);

}

//Starting after 1.0s to make sure the page is loaded
setTimeout(main, 1000);
